// Say we have a word "hell", and "hello", and we test against "hello", it will match against "hell" first.
func sortByLength(s []string) {
	sort.Slice(s, func(i, j int) bool {
		return len(s[i]) > len(s[j])
	})
}

// Add adds a new words into the profanity list.
func Add(words ...string) {
	tmp = append(tmp, words...)
	sortByLength(tmp)
	re = regexp.MustCompile("(?i)" + strings.Join(tmp, "|"))
}

// Reset resets the list back to the original list.
func Reset() {
	tmp = make([]string, len(Words))
	copy(tmp, Words)
	sortByLength(tmp)
	re = regexp.MustCompile(strings.Join(tmp, "|"))
}

// Init creates a new regex based on the provided list.
func Init(words ...string) {
	tmp = make([]string, len(words))
	copy(tmp, words)
	sortByLength(tmp)
	re = regexp.MustCompile(strings.Join(tmp, "|"))
}

// ReplaceGarbled replaces profane words with $@!#%.
func ReplaceGarbled(s string) string {
	return re.ReplaceAllStringFunc(s, func(s string) string {
		return "$@!#%"
	})
}

// ReplaceStars replaces profane words with '*' up to the word's length.
func ReplaceStars(s string) string {
	return re.ReplaceAllStringFunc(s, func(s string) string {
		if len(s) < 3 {
			return strings.Repeat("*", len(s))
		}
		head := s[:1]
		body := strings.Repeat("*", len(s)-2)
		tail := s[len(s)-1:]
		return fmt.Sprintf("%s%s%s", head, body, tail)
	})
}

// ReplaceVowels replaces the vowels in the profane word with '*'.
func ReplaceVowels(s string) string {
	return re.ReplaceAllStringFunc(s, func(s string) string {
		return vowels.ReplaceAllString(s, "*")
	})
}

// ReplaceNonConsonants replaces non consonants with '*'.
func ReplaceNonConsonants(s string) string {
	return re.ReplaceAllStringFunc(s, func(s string) string {
		return nonconsonants.ReplaceAllString(s, "*")
	})
}

// ReplaceCustom replaces the profane word with the custom string.
func ReplaceCustom(s, t string) string {
	return re.ReplaceAllStringFunc(s, func(string) string {
		return t
	})
}
func main() {
	Add("hello")
	fmt.Println(ReplaceStars("HELLO world"))
}
